class BookForm extends React.Component {

  state = {}

  render(){
    return(
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" />
        </label>
        <label>
          Author:
          <input type="text" name="author" />
        </label>
        <label>
          Image:
          <input type="text" name="img" />
        </label>
        <input type="submit" value="Submit" />
      </form>
    )
  }
}

export default BookForm;
